insert into coffee_catalogue (name, description, price) values ('Cuba Serano', 'Chuťový profil: V šálke je charakteristická orieškovou krémou a likérovou arómou. Chuť je jemne sladká, s príjemným sirupovým telom a veľmi ľahkou, takmer nebadateľnou aciditou. V dochuti môžete cítiť náznak karamelu a nezriedka i tabaku.', 17);
insert into coffee_catalogue (name, description, price) values ('Columbia Mercedes', 'Chuťový profil: V šálke je charakteristická jemnou krémou a arómou po čerstvo upečenom chlebe. Chuť je príjemne sladká, často s tónmi orieškov, čo v dochuti vytvára zaujímavé, horko–sladké čokoládové chute.', 15);
insert into coffee_catalogue (name, description, price) values ('Kenya Kilimanjaro', 'Chuťový profil: V šálke je charakteristická svetlejšou krémou a ľahkou citrusovou arómou. Chuť je mierne acidná, s nádychom citrusových plodov, černíc a niekedy aj čierneho korenia. V dochuti cítiť stále jemné citrusovo – korenisté tóny.', 17);
insert into coffee_catalogue (name, description, price) values ('Ethiopia Sidamo', 'Chuťový profil: V šálke je charakteristická orieškovo-hnedou krémou a delikátnou arómou s tónmi jazmínu a lupeňov ruže. Chuť je likérovo sladká, s jemnými náznakmi citrusového ovocia. V dlhotrvajúcej dochuti cítiť stále citrusovú sladkosť.', 17);
